package com.example.keepit;import android.annotation.SuppressLint;import android.content.Intent;import android.os.Bundle;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import androidx.activity.EdgeToEdge;import androidx.appcompat.app.AppCompatActivity;import androidx.core.graphics.Insets;import androidx.core.view.ViewCompat;import androidx.core.view.WindowInsetsCompat;public class LoginActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login);        EditText email = findViewById(R.id.et_email);        EditText pass = findViewById(R.id.et_password);        Button btn = findViewById(R.id.btn_login);        TextView signin = findViewById(R.id.tv_signin);        signin.setOnClickListener(v -> {            Intent intent = new Intent(LoginActivity.this, SignupActivity.class);            startActivity(intent);        });        btn.setOnClickListener(v -> {            String user = "test@gmail.com";            String password = "12345";            if (email.getText().toString().equals(user) && pass.getText().equals(password)) {                Intent intent = new Intent(LoginActivity.this, WelcomeActivity.class);                intent.putExtra("email",user);                startActivity(intent);            } else {                Toast.makeText(this, "wrong password or username", Toast.LENGTH_SHORT).show();            }        });}}