package com.example.keepit;import android.content.Intent;import android.os.Bundle;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;public class SignupActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {       super.onCreate(savedInstanceState);      setContentView(R.layout.activity_signup);        EditText email = findViewById(R.id.et_email);        EditText pass = findViewById(R.id.et_password);        EditText pass_conf = findViewById(R.id.et_confirm_password);        Button signin = findViewById(R.id.btn_signin);        Button cancel = findViewById(R.id.btn_cancel);        signin.setOnClickListener(v -> {            Toast.makeText(this,"A signin request was sent to the server",Toast.LENGTH_SHORT).show();        });        cancel.setOnClickListener(v -> {            Intent intent = new Intent(SignupActivity.this,LoginActivity.class);            startActivity(intent);        });   }}