package com.example.keepit.activities;import android.content.Intent;import android.os.Bundle;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import com.example.keepit.R;import com.example.keepit.database.UsersDatabase;public class LoginActivity extends AppCompatActivity {    private UsersDatabase usersDatabase;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login);        usersDatabase = new UsersDatabase(this);        EditText email = findViewById(R.id.et_email);        EditText pass = findViewById(R.id.et_password);        Button btn = findViewById(R.id.btn_login);        TextView signin = findViewById(R.id.tv_signin);        signin.setOnClickListener(v -> {            Intent intent = new Intent(LoginActivity.this, SignupActivity.class);            startActivity(intent);        });        btn.setOnClickListener(v -> {            String emailInput = email.getText().toString().trim();            String passwordInput = pass.getText().toString().trim();            if (emailInput.isEmpty() || passwordInput.isEmpty()) {                Toast.makeText(this, "Please enter both email and password", Toast.LENGTH_SHORT).show();                return;            }            boolean isValidUser = usersDatabase.checkUser(emailInput, passwordInput);            if (isValidUser) {                Intent intent = new Intent(LoginActivity.this, WelcomeActivity.class);                intent.putExtra("email", emailInput);                startActivity(intent);            } else {                Toast.makeText(this, "Wrong email or password", Toast.LENGTH_SHORT).show();            }        });    }}